import { jsx as _jsx } from "react/jsx-runtime";
import MFATotpView from '../MFATotpSetupView';
import { useCallback } from 'react';
const MFATotpSetup = ({ cognitoUser, resetView, secretCode }) => {
    const handleConfirm = useCallback(async (data) => {
        return new Promise((resolve, reject) => {
            cognitoUser.verifySoftwareToken(data.confirmationCode, '', {
                onSuccess() {
                    resolve({});
                    resetView();
                },
                onFailure(err) {
                    console.error('Cognito verifySoftwareToken Failure', err);
                    reject(err);
                },
            });
        });
    }, [resetView, cognitoUser]);
    return _jsx(MFATotpView, { secretCode: secretCode, onConfirm: handleConfirm, onBackToSignIn: resetView });
};
export default MFATotpSetup;
