import { jsx as _jsx } from "react/jsx-runtime";
import { componentTypes } from '../../../FormRenderer';
import GenericView from '../GenericView';
const schema = {
    header: 'Reset Password',
    headerVariant: 'h2',
    variant: 'embedded',
    canCancel: false,
    submitLabel: 'Send code',
    fields: [
        {
            component: componentTypes.TEXT_FIELD,
            isRequired: true,
            label: 'Username',
            name: 'username',
            placeholder: 'Enter your username',
            validate: [
                {
                    type: 'required',
                },
            ],
        },
    ],
};
const ForgotPasswordUsernameView = ({ onSendCode, onBackToSignIn }) => {
    return _jsx(GenericView, { schema: schema, onSubmit: onSendCode, onBackToSignIn: onBackToSignIn });
};
export default ForgotPasswordUsernameView;
