import Button from '@cloudscape-design/components/button';
import { FC, PropsWithChildren, useCallback } from 'react';

export interface CopyToClipbordProps {
  /**
   * Specifies the text content to be copied instead of what is inside the children.
   */
  content?: string;
}

const CopyToClipbord: FC<PropsWithChildren<CopyToClipbordProps>> = ({ children, content }) => {
  const handleClick = useCallback(async () => {
    if (content) {
      await navigator.clipboard.writeText(content);
    } else if (typeof children === 'string') {
      await navigator.clipboard.writeText(children);
    }
  }, [children, content]);

  return <><Button iconName="copy" onClick={handleClick} variant="inline-icon" />{children}</>;
};

export default CopyToClipbord;
