import { FC } from 'react';
import { ProgressBarProps } from '@cloudscape-design/components/progress-bar';
import { KeyValuePairProps } from '../KeyValuePair';
export interface KeyValuePairWithProgressBarProps extends Pick<ProgressBarProps, 'label' | 'description' | 'value' | 'additionalInfo'> {
    variant: 'key-value';
}
export interface KeyValuePairsProps {
    /** A list of key value pairs array. Each array will be displayed in a column.
     */
    items: (KeyValuePairProps | KeyValuePairWithProgressBarProps)[][];
}
/**
 * Key-value pairs are lists of properties (keys) followed by the corresponding value.
 */
declare const KeyValuePairs: FC<KeyValuePairsProps>;
export default KeyValuePairs;
